mapper.select:
select bno, title, content, writer, regdate, updatedate
from board
order by updatedate, bno DESC;

select bno, title, content, writer, regdate, updatedate
from board
where bno = #{bno};

mapper.update:
update board_password
set password   = #{password}
where bno = #{bno}

mapper.select:
alter table board
    auto_increment = 1;
set @COUNT = 0;
update board
set bno = @COUNT := @COUNT + 1
where bno > 0;
select Auto_increment
from information_schema.tables
where TABLE_SCHEMA = 'board_db'
  and TABLE_NAME = 'board';
