mapper.select:
select count(*) as count
from posts
where board_no = #{boardNo};

select pno, board_no, type, title, contents, writer, reg_date, update_date
from posts
where board_no = #{board_no}
order by update_date, pno DESC;

select pno, board_no, type, title, contents, writer, reg_date, update_date
from posts

    where type = #{type}

order by update_date desc
limit ${(page - 1) * 10}, 10;

select pno, type, title, contents, writer, reg_date, update_date
from posts
where pno = #{pno};
