mapper.select:
select userid, authority
from users_authorities
where userid like #{userId};

mapper.delete:
delete
from users
where users.userid like #{userId};

mapper.update:
update users
set password = #{password},
    username = #{userName}
where userid = #{userId};

mapper.delete:
delete
from users_authorities
where userid like #{userId}
  and authority like #{authority};

delete
from users_authorities
where userid like #{userId};

mapper.update:
update users
set enabled = false
where userid like #{userId};

update users
set enabled = true
where userid like #{userId};

mapper.select:
select password
from users
where userid = #{userId};
