mapper.select:
select count(post_no) as count


        from (
        select post_no
        from posts
        limit ${(page - 1) * 10}, 10
        ) as by_page


        from posts




        board_no = #{boardNo}


        and type like #{type}

select post_no, board_no, type, title, contents, writer, reg_date, update_date from posts
board_no = #{boardNo}
AND type like #{type}
AND (title like concat('%', #{keyword}, '%') OR contents like concat('%', #{keyword}, '%'))
limit ${(page - 1) * 10}, 10
order by reg_date desc

select post_no, board_no, type, title, contents, writer, reg_date, update_date
from posts
where post_no = #{pno};

select if(count(*) = 1, 1, 0)
from posts
where post_no = #{pno} and writer like #{userId}
