mapper.select:
select user_id, password, nickname, enabled, reg_date, update_date
from users
where users.user_id = #{user_id};

mapper.delete:
delete
from users
where user_id like #{user_id};

mapper.update:
update users
set enabled = true
where user_id like #{user_id};

update users
set enabled = false
where user_id like #{user_id};

mapper.select:
select if(count(user_id), 0, 1)
from users
where user_id like #{user_id};

select if(count(nickname), 0, 1)
from users
where nickname like #{nickname};
