mapper.select:
set @COUNT = 0;


        alter table boards
        auto_increment = 1;
        update boards
        set bno = @COUNT := @COUNT + 1
        where bno > 0;


        alter table posts
        auto_increment = 1;
        update posts
        set pno = @COUNT := @COUNT + 1
        where pno > 0;


select Auto_increment
from information_schema.tables
where TABLE_SCHEMA = 'board_db'
and TABLE_NAME = #{tableName};
